/* pretty print json */
func prettyPrintJSON(jsonElement []byte) string {
	var obj any
	if err := json.Unmarshal([]byte(jsonElement), &obj); err != nil {
		return string(jsonElement)
	}
	pretty, err := json.MarshalIndent(obj, "", "  ")
	if err != nil {
		return string(jsonElement)
	}
	return string(pretty)
}

